import Post from "../models/Post";

export const getAllPosts = async (req, res) => {
  let posts;
  try {
    posts = await Post.find();
  } catch (error) {
    return console.log(error);
  }

  if (!posts) {
    return res.status(500).json({ message: "unexpected error occur" });
  }

  return res.status(200).json({ posts });
};

export const addPost = async (req, res) => {
  const { title, description, image, location, date, user } = req.body;

  if (
    !title &&
    title.trim() === "" &&
    !description &&
    description.trim() === "" &&
    !location &&
    location.trim() === "" &&
    !date &&
    !user &&
    !image &&
    image.trim() === ""
  ) {
    return res.status(422).json({ message: "invalid data" });
  }

  let post;
  try {
    post = new Post({
      title,
      description,
      image,
      date: new Date(`$${date}`),
      location,
      user,
    });
    post = await post.save();
  } catch (error) {
    return console.log(error);
  }

  if (!post) {
    return res.status(500).json({ message: "unexpected error ocuur" });
  }

  return res.status(201).json({ post });
};

export const getPostById = async (req, res) => {
  const id = req.params.id;

  let post;
  try {
    post = await Post.findById(id);
  } catch (error) {
    return console.log(error);
  }

  if (!post) {
    return res.status(404).json({ message: "No post found" });
  }

  return res.status(200).json({ post });
};

export const updatePost = async (req, res) => {
  const id = req.params.id;
  const { title, description, image, location, date } = req.body;

  if (
    !title &&
    title.trim() === "" &&
    !description &&
    description.trim() === "" &&
    !location &&
    location.trim() === "" &&
    !date &&
    !image &&
    image.trim() === ""
  ) {
    return res.status(422).json({ message: "invalid data" });
  }

  let post;
  try {
    post = await Post.findByIdAndUpdate(id, {
      title,
      description,
      image,
      date: new Date(`${date}`),
      location,
    });
  } catch (error) {
    return console.log(error);
  }

  if(!post){
    return res.status(500).json({message:"unable to upate"})
  }

  return res.status(200).json({message:"updated successfully"})
};

export const deletePost=async(req,res)=>{
    let id=req.params.id;
    
    let post;
    try {
        post=await Post.findByIdAndRemove(id);
    } catch (error) {
        return console.log(error)
    }

    if(!post){
        return res.status(500).json({message:"unable to delete"})
    }

    return res.status(200).json({message:"Deleted successfully"})
}

